import express from 'express'
import bp from './apis/bannerP'
import { readF, writeF } from './apis/utils'
const app = express()

app.use(express.static(__dirname + '/public'))
app.use(express.json())

app.get('/getAll', (req, res) => {
    let result = readF(bp);
    res.send(result);
})


app.post('/del', (req, res) => {
    let { index } = req.body;
    let result = readF(bp);
    let indexArr = index.split('-'); //[1,1]
    // result[indexArr[0]].splice(indexArr[1],1) 
    for (let k in result[indexArr[0]]) {
        result[indexArr[0]][k].splice(indexArr[1], 1)
    }
    writeF(bp, result);
    res.send('删除成功');
})

// add
app.post('/add', (req, res) => {
    let { info } = req.body;
    let result = readF(bp);
    let coms = [];
    result.forEach((item) => {
        for (let k in item) {
            coms.push(k)
        }
    })
    console.log(coms); //[ 'A公司', 'C公司' ]

    for (let i = 0; i < coms.length; i++) {
        if (coms[i] === info.gongsi) {
            for (let k in result[i]) {
                result[i][k].push(info)
                writeF(bp, result)
                res.send('OK');
                return;
            }
        } else {
            result.push({
                [info.gongsi]: [info]
            })
            writeF(bp, result)
            res.send('OK');
            return;
        }
    }


})

// edit
app.post('/edit', (req, res) => {
    let { info, editIndex } = req.body;
    let result = readF(bp);
    let arr = editIndex.split('-');
    for (let k in result[arr[0]]) {
        result[arr[0]][k][arr[1]] = info;
    }
    writeF(bp, result);
    res.send('OK');
})

//重定向
app.get('/', (req, res) => {
    res.redirect('/banner.html')
})

app.listen(3000, () => {
    console.log('3000');
})

